.section-hero-pricing-combo-v1 {
   /* Orange */
   /* Dark Grey */
  --color-bg: #f3f4f6; /* Light Gray */
  --color-white: #ffffff;


  position: relative;
  background-color: var(--color-bg);
  padding-bottom: 4rem;
  font-family: var(--font-body);
}

/* Split Background */
.section-hero-pricing-combo-v1 .bg-split {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 60%;
  background-color: var(--color-primary);
  z-index: 0;
}

.section-hero-pricing-combo-v1 .container {
  position: relative;
  z-index: 1;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}

/* Hero Row */
.section-hero-pricing-combo-v1 .hero-row {
  display: flex;
  justify-content: center;
  padding-top: 4rem;
  padding-bottom: 2rem;
}

.section-hero-pricing-combo-v1 .image-wrapper {
  position: relative;
  display: inline-block;
  max-width: 800px;
  width: 100%;
}

.section-hero-pricing-combo-v1 .hero-img {
  width: 100%;
  height: auto;
  display: block;
  max-height: 400px;
  object-fit: cover;
  box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1);
}

.section-hero-pricing-combo-v1 .hero-text-card {
  position: absolute;
  top: -20px;
  right: -20px;
  background-color: var(--color-secondary);
  color: var(--color-white);
  padding: 2.5rem;
  max-width: 350px;
  box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.3);
}

.section-hero-pricing-combo-v1 .hero-subtitle {
  text-transform: uppercase;
  letter-spacing: 1px;
  font-size: 0.875rem;
  opacity: 0.8;
  margin-bottom: 0.5rem;
}

.section-hero-pricing-combo-v1 .hero-title {
  font-family: var(--font-heading);
  font-size: 2.5rem;
  line-height: 1.1;
  margin-bottom: 1.5rem;
  font-weight: 700;
}

.section-hero-pricing-combo-v1 .image-credit {
  font-size: 0.75rem;
  opacity: 0.5;
  text-decoration: underline;
}

/* Pricing Row */
.section-hero-pricing-combo-v1 .pricing-row {
  display: flex;
  justify-content: center;
  gap: 1.5rem;
  margin-top: 2rem;
  flex-wrap: wrap;
}

.section-hero-pricing-combo-v1 .price-col {
  flex: 1;
  min-width: 300px;
  max-width: 350px;
}

.section-hero-pricing-combo-v1 .pricing-card {
  background: var(--color-white);
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
  display: flex;
  flex-direction: column;
  height: 100%;
}

.section-hero-pricing-combo-v1 .card-header {
  padding: 3rem 1.5rem;
  text-align: center;
  color: var(--color-white);
}

.section-hero-pricing-combo-v1 .dark-header {
  background-color: var(--color-secondary);
}

.section-hero-pricing-combo-v1 .primary-header {
  background-color: var(--color-primary);
}

.section-hero-pricing-combo-v1 .plan-name {
  font-size: 1.25rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
}

.section-hero-pricing-combo-v1 .price-block {
  font-family: var(--font-heading);
  font-weight: 700;
  font-size: 3.5rem;
  line-height: 1;
  margin-bottom: 0.5rem;
}

.section-hero-pricing-combo-v1 .currency {
  font-size: 2rem;
  vertical-align: super;
}

.section-hero-pricing-combo-v1 .period {
  font-size: 0.875rem;
  opacity: 0.9;
}

.section-hero-pricing-combo-v1 .card-body {
  padding: 2rem;
  text-align: center;
  flex-grow: 1;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

.section-hero-pricing-combo-v1 .feature-list {
  list-style: none;
  padding: 0;
  margin: 0 0 2rem 0;
}

.section-hero-pricing-combo-v1 .feature-list li {
  padding: 0.5rem 0;
  border-bottom: 1px solid #e5e7eb;
  font-size: 0.95rem;
  color: #4b5563;
}

.section-hero-pricing-combo-v1 .feature-list li:last-child {
  border-bottom: none;
}

.section-hero-pricing-combo-v1 .btn-plan {
  background-color: #d1d5db;
  color: #111827;
  border: none;
  padding: 0.75rem 1.5rem;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  cursor: pointer;
  transition: background 0.3s;
  width: 100%;
}

.section-hero-pricing-combo-v1 .btn-plan:hover {
  background-color: #9ca3af;
}

/* Responsive */
@media (max-width: 992px) {
  .section-hero-pricing-combo-v1 .hero-text-card {
    position: relative;
    top: 0;
    right: 0;
    max-width: 100%;
    margin-top: -3rem;
    margin-left: 1rem;
    margin-right: 1rem;
  }

  .section-hero-pricing-combo-v1 .image-wrapper {
    display: flex;
    flex-direction: column;
  }
}

@media (max-width: 768px) {
  .section-hero-pricing-combo-v1 .bg-split {
    height: 40%;
  }

  .section-hero-pricing-combo-v1 .pricing-row {
    flex-direction: column;
    align-items: center;
  }

  .section-hero-pricing-combo-v1 .price-col {
    width: 100%;
    max-width: 400px;
  }
}
