css:
.section-pricing-flexible {
  --color-bg: #e0e0e0;



  --color-essential-bg: #d1d1d1;
  --color-elite-bg: #dcbcc0;
  --color-advanced-bg: #dcd5d0;


  --radius-card: 24px;

  background-color: var(--color-bg);
  padding: 60px 20px;
  font-family: var(--font-body);
  color: var(--color-dark);
}

.section-pricing-flexible .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 15px;
}

.section-pricing-flexible .pricing-header {
  text-align: center;
  margin-bottom: 50px;
}

.section-pricing-flexible .pre-title {
  color: var(--color-primary);
  font-weight: 600;
  font-size: 1.1rem;
  display: block;
  margin-bottom: 10px;
}

.section-pricing-flexible .title {
  font-family: var(--font-heading);
  font-size: 3rem;
  font-weight: 700;
  margin: 0;
  color: #000;
}

.section-pricing-flexible .row-layout {
  display: flex;
  flex-direction: row;
  justify-content: center;
  gap: 30px;
  align-items: stretch;
}

.section-pricing-flexible .col-item {
  flex: 1;
  min-width: 300px;
  display: flex;
}

.section-pricing-flexible .pricing-card {
  border-radius: var(--radius-card);
  padding: 40px 30px;
  display: flex;
  flex-direction: column;
  width: 100%;
  position: relative;
  transition: transform 0.3s ease;
}

.section-pricing-flexible .pricing-card:hover {
  transform: translateY(-5px);
}

/* Card Variants */
.section-pricing-flexible .card-essential {
  background-color: var(--color-essential-bg);
}
.section-pricing-flexible .card-elite {
  background-color: var(--color-elite-bg);
}
.section-pricing-flexible .card-advanced {
  background-color: var(--color-advanced-bg);
}

.section-pricing-flexible .pkg-name {
  font-family: var(--font-heading);
  font-size: 1.2rem;
  font-weight: 600;
  margin-bottom: 15px;
  color: #000;
}

.section-pricing-flexible .price-block {
  display: flex;
  align-items: baseline;
  gap: 8px;
  margin-bottom: 15px;
}

.section-pricing-flexible .amount {
  font-size: 3.5rem;
  font-weight: 700;
  line-height: 1;
  color: #000;
}

.section-pricing-flexible .period {
  font-size: 1.1rem;
  font-weight: 500;
  color: #333;
}

.section-pricing-flexible .pkg-desc {
  font-size: 1rem;
  line-height: 1.4;
  margin-bottom: 25px;
  min-height: 50px;
  color: #333;
  font-weight: 500;
}

.section-pricing-flexible .divider {
  height: 1px;
  background-color: rgba(0,0,0,0.15);
  margin-bottom: 25px;
  width: 100%;
}

.section-pricing-flexible .feature-list {
  list-style: none;
  padding: 0;
  margin: 0 0 30px 0;
  flex-grow: 1;
}

.section-pricing-flexible .feature-list li {
  display: flex;
  align-items: flex-start;
  gap: 12px;
  margin-bottom: 18px;
  font-size: 0.95rem;
  line-height: 1.5;
  color: #222;
}

.section-pricing-flexible .icon {
  background-color: var(--color-primary);
  color: white;
  border-radius: 50%;
  width: 20px;
  height: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 10px;
  flex-shrink: 0;
  margin-top: 2px;
}

.section-pricing-flexible .card-footer {
  margin-top: auto;
}

.section-pricing-flexible .btn-choose {
  background-color: var(--color-primary);
  color: white;
  border: none;
  padding: 14px 28px;
  border-radius: 30px;
  font-weight: 600;
  font-size: 1rem;
  cursor: pointer;
  transition: background-color 0.2s;
  width: auto;
  min-width: 160px;
}

.section-pricing-flexible .btn-choose:hover {
  opacity: 0.9;
}

@media (max-width: 991px) {
  .section-pricing-flexible .row-layout {
    flex-direction: column;
    align-items: center;
  }

  .section-pricing-flexible .col-item {
    width: 100%;
    max-width: 500px;
  }

  .section-pricing-flexible .title {
    font-size: 2.2rem;
  }
}
